// Change the text of an element
const heading = document.getElementById('mainHeading');
heading.textContent = 'Welcome to the DOM!';

// Change the color of an element
heading.style.color = 'blue';

// Select by ID and change text
const element = document.getElementById('myId');
element.textContent = 'Changed by ID';

// Select by class name and change color
const elements = document.getElementsByClassName('myClass');
for (let el of elements) {
  el.style.color = 'red';
}

// Select by tag name and add border
const paragraphs = document.getElementsByTagName('p');
for (let p of paragraphs) {
  p.style.border = '1px solid black';
}

// Select using CSS selector and change background
const firstElement = document.querySelector('.myClass');
firstElement.style.backgroundColor = 'yellow';

// Select all matching elements and add padding
const allElements = document.querySelectorAll('.myClass');
allElements.forEach(el => el.style.padding = '10px');

// Create a new paragraph element
const newParagraph = document.createElement('p');

// Add text to the paragraph
newParagraph.textContent = 'This is a new paragraph.';

// Add the new paragraph to the body
document.body.appendChild(newParagraph);

// Show the updated body content
const bodyContent = document.body.innerHTML;
document.getElementById('output').textContent = bodyContent;

// Select the element to remove
const elementToRemove = document.getElementById('removeMe');

// Remove the element
elementToRemove.remove();

// Show the updated body content
const bodyContent = document.body.innerHTML;
document.getElementById('output').textContent = bodyContent;

const button = document.getElementById('myButton');
const output = document.getElementById('output');

button.addEventListener('click', function() {
  output.textContent = 'Button was clicked!';
});

// You can click the button to see the result

function handleClick() {
  output.textContent = 'Button was clicked!';
}

button.addEventListener('click', handleClick);

// Later, when you want to remove the event listener:
button.removeEventListener('click', handleClick);

output.textContent = 'Event listener removed. Try clicking the button now.';

