h1 {
  background: lightblue;
}

/* General styling */
body {
  font-family: Arial, sans-serif;
}

/* Header section with CodeCraft branding color */
.header-section {
  background-color: #1a1a2e; /* Dark blue for header */
  color: #f8f9fa; /* Light text color */
  padding: 20px;
  text-align: center;
}

/* Courses section with a vibrant color */
.courses-section {
  background-color: #16213e; /* Slightly lighter blue */
  color: #e94560; /* Bright accent color */
  padding: 20px;
  margin: 20px 0;
}

/* Community section with a welcoming color */
.community-section {
  background-color: #0f3460; /* Another shade of blue */
  color: #f8f9fa; /* Light text color */
  padding: 20px;
  margin: 20px 0;
}

body {
    background-image: url("https://img.freepik.com/premium-vector/elearning-concept-laptop-as-book-dark-blue-background-online-education-technology_387612-38.jpg");
    background-repeat: repeat; /* Default value, repeats both horizontally and vertically */
}

body {
    background-image: url("https://img.freepik.com/premium-vector/elearning-concept-laptop-as-book-dark-blue-background-online-education-technology_387612-38.jpg");
    background-repeat: repeat-x; /* Repeats only horizontally */
}

body {
    background-image: url("https://img.freepik.com/premium-vector/elearning-concept-laptop-as-book-dark-blue-background-online-education-technology_387612-38.jpg");
    background-repeat: no-repeat; /* Image does not repeat */
}

body {
    height: 100vh; /* Full viewport height */
    background-image: url("https://img.freepik.com/premium-vector/elearning-concept-laptop-as-book-dark-blue-background-online-education-technology_387612-38.jpg");
    background-repeat: space; /* Distributes the space evenly between repeated images */
}

body {
    height: 100vh; /* Full viewport height */
    background-image: url("https://img.freepik.com/premium-vector/elearning-concept-laptop-as-book-dark-blue-background-online-education-technology_387612-38.jpg");
    background-repeat: round; /* Resizes the image to fill the background area */
}

body {
  background-image: url('https://campuslabs.in/wp-content/uploads/2020/07/e-learning-bg.jpg');
  background-attachment: scroll; /* Default value: image scrolls with the content */
  background-repeat: no-repeat; /* Ensures the image does not repeat */
  background-size: cover; /* Scales the image to cover the entire background */
}

body {
  background-image: url('https://campuslabs.in/wp-content/uploads/2020/07/e-learning-bg.jpg');
  background-attachment: fixed; /* Background image remains fixed during scrolling */
  background-repeat: no-repeat; /* Ensures the image does not repeat */
  background-size: cover; /* Scales the image to cover the entire background */
}

.scrolling-content {
  height: 200px; /* Define a height for the container */
  overflow: auto; /* Enable scrolling */
  background-image: url('https://media.licdn.com/dms/image/D4D12AQHL1tmi-7TC4A/article-cover_image-shrink_720_1280/0/1690022978374?e=2147483647&v=beta&t=YwKKeReRpB5gpkENmHGOi4zjgRuaKJqSKJOKXuNTIMc');
  background-attachment: local; /* Background image scrolls with the container’s content */
  background-repeat: no-repeat; /* Ensures the image does not repeat */
  background-size: cover; /* Scales the image to cover the entire background */
}

.fixed-size {
  background-image: url('https://c8.alamy.com/comp/2BKM4PB/e-learning-online-school-vector-background-e-learning-online-courses-text-with-computer-devices-and-school-elements-for-digital-home-education-study-2BKM4PB.jpg');
  background-size: 300px 200px; /* Set background image width to 300px and height to 200px */
  background-repeat: no-repeat; /* Prevent image from repeating */
  width: 400px; /* Container width */
  height: 300px; /* Container height */
}

.cover-background {
  background-image: url('https://www.collegiateparent.com/wp-content/uploads/2020/04/online-learning-terms.jpg');
  background-size: cover; /* Ensure the image covers the entire container */
  background-repeat: no-repeat; /* Prevent image from repeating */
  width: 100vw; /* Full viewport width */
  height: 100vh; /* Full viewport height */
}

.clip-border {
  padding: 15px;
  border: 10px solid black;
  background-color: lightblue;
  background-clip: border-box; /* Background extends to the border edge */
  text-align: center;
}

.clip-padding {
  padding: 15px;
  border: 10px dashed black; /* Dashed border to show break */
  background-color: lightcoral;
  background-clip: padding-box; /* Background stops at the padding edge */
  text-align: center;
  width: 200px;
  margin: 20px;
}

.clip-content {
  padding: 15px;
  border: 10px solid black;
  background-color: lightgreen;
  background-clip: content-box; /* Background only in the content area */
  text-align: center;
}

.clip-text {
  background-image: url('https://c0.wallpaperflare.com/preview/746/150/495/computer-concept-education-illustration.jpg');
  background-clip: text; /* Background only in the text area */
  -webkit-background-clip: text; /* Browser compatibility */
  -webkit-text-fill-color: transparent; /* Text color transparent */
  font-size: 36px;
  font-weight: bold;
}

.border-box {
    border: 10px solid black;
    padding: 20px;
    background-image: url('https://png.pngtree.com/thumb_back/fh260/back_our/20190617/ourmid/pngtree-blue-technology-e-commerce-learning-education-business-poster-banner-image_123753.jpg');
    background-repeat: no-repeat;
    background-origin: border-box; /* Background starts from the border edge */
}

.padding-box {
    border: 10px dashed black; /* Dashed border to show break */
    padding: 20px;
    background-image: url('https://cdn.elearningindustry.com/wp-content/uploads/2023/02/shutterstock_1449924503.jpg');
    background-repeat: no-repeat;
    background-origin: padding-box; /* Background starts from the padding edge */
}

.content-box {
    border: 10px solid black;
    padding: 20px;
    background-image: url('https://www.shutterstock.com/image-photo/banner-homeschooling-workspace-laptop-stationery-260nw-1887490225.jpg');
    background-repeat: no-repeat;
    background-origin: content-box; /* Background starts from the content edge */
}

.fixed-background {
    border: 10px solid black;
    padding: 20px;
    background-image: url('https://img.freepik.com/free-photo/flat-lay-stationary-arrangement-desk-with-yellow-supplies_23-2148404491.jpg');
    background-repeat: no-repeat;
    background-size: cover;
    background-attachment: fixed; /* Background is fixed relative to the viewport */
    background-origin: content-box; /* This does not affect the fixed background */
}

.position-length {
  height: 100vh;
  background-image: url('https://tecnosoluciones.com/wp-content/uploads/2019/03/e-Learning-y-sus-caracteristicas.jpg'); /* Placeholder image URL */
  background-repeat: no-repeat;
  background-position: 100px 50px; /* Positions image 100px from the left and 50px from the top */
}

.position-percentage {
  height: 100vh;
  background-image: url('https://tecnosoluciones.com/wp-content/uploads/2019/03/e-Learning-y-sus-caracteristicas.jpg'); /* Placeholder image URL */
  background-repeat: no-repeat;
  background-position: 10% 50%; /* Positions image 10% from the left and 50% from the top */
}

.position-keywords {
  height: 100vh;
  background-image: url('https://tecnosoluciones.com/wp-content/uploads/2019/03/e-Learning-y-sus-caracteristicas.jpg'); /* Placeholder image URL */
  background-repeat: no-repeat;
  background-position: top right; /* Positions image at the top-right corner */
}

.second {
  opacity: 0.5; /* 50% transparent */
}

.box {
    height: 100px;
    color: white;
    text-align: center;
    line-height: 100px;
    font-size: 20px;
}

.box1 {
    background-color: rgba(173, 216, 230, 1); /* Fully opaque light blue */
}

.box2 {
    background-color: rgba(173, 216, 230, 0.7); /* 70% opaque light blue */
}

.box3 {
    background-color: rgba(173, 216, 230, 0.3); /* 30% opaque light blue */
}

.box4 {
    background-color: rgba(173, 216, 230, 0.1); /* 10% opaque light blue */
}

.hover-image {
    width: 350px;
    margin: 8px;
    opacity: 0.6; /* 60% opaque */
    transition: opacity 0.3s;
}

.hover-image:hover {
    opacity: 1; /* Fully opaque on hover */
}
